Eigen::Vector3d SphereAtlas::random_projection() {
  auto element = Eigen::Vector3d::Random();
  Eigen::Vector3d result;
  result(0) =
      element(0) / std::sqrt(std::pow(element(0), 2) + std::pow(element(1), 2) +
                             std::pow(element(2), 2));
  result(1) =
      element(1) / std::sqrt(std::pow(element(0), 2) + std::pow(element(1), 2) +
                             std::pow(element(2), 2));
  result(2) =
      element(2) / std::sqrt(std::pow(element(0), 2) + std::pow(element(1), 2) +
                             std::pow(element(2), 2));
  return result;
}

